export const translations: Record<string, Record<string, string>> = {};

languages.forEach(lang => {
  const code = lang.code;
  translations[code] = {};
  
  // Apply UI Strings
  Object.entries(UI_STRINGS).forEach(([key, val]) => {
    translations[code][key] = val[code] || val['en'] || key;
  });
  
  // Apply Item Names
  Object.entries(itemNames).forEach(([id, val]) => {
    translations[code][`item.name.${id}`] = val[code] || val['en'] || id;
  });

  // Apply Item Descriptions with fallback
  Object.entries(itemNames).forEach(([id]) => {
    const desc = itemDescs[id] || itemDescs['default'];
    translations[code][`item.desc.${id}`] = desc[code] || desc['en'] || 'Quality item';
  });
});